mapper.update:
update t_emp
set name = #{name,jdbcType=VARCHAR},
  path = #{path,jdbcType=VARCHAR},
  salary = #{salary,jdbcType=DOUBLE},
  age = #{age,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, name, path, salary, age
from t_emp
where id = #{id,jdbcType=INTEGER}
